mapper.select:
select id, project_price
from order_info_v
where order_id = #{orderId,jdbcType=VARCHAR}

select
order_id , order_status state, order_created_at created_at,
driver_name ,project_descp, project_type
from order_info_v
where user_id = #{map.userId,jdbcType=INTEGER}
and order_type = #{map.orderType,jdbcType=VARCHAR}

    and order_status = #{map.state,jdbcType=VARCHAR}


    and order_created_at BETWEEN #{map.fromDate,jdbcType=TIMESTAMP} and #{map.toDate,jdbcType=TIMESTAMP}

select
count(*)
from order_info_v
where user_id = #{map.userId,jdbcType=INTEGER}
and order_type = #{map.orderType,jdbcType=VARCHAR}

    and order_status = #{map.state,jdbcType=VARCHAR}


    and order_created_at BETWEEN #{map.fromDate,jdbcType=TIMESTAMP} and #{map.toDate,jdbcType=TIMESTAMP}

select id
from ykat_projects
where ykat_projects.type = #{projectType,jdbcType=VARCHAR}
