mapper.select:
select setRecords.set_acc_id,setRecords.set_acc_id rev_mon_id, drivers.driver_name, setRecords.trade_money*0.9 actual_money, setRecords.traded_at from ykat_settle_account_records setRecords inner join ykat_orders orders on setRecords.order_id = orders.id inner join ykat_drivers drivers on orders.driver_id = drivers.id inner join ykat_stores stores on orders.store_id = stores.id where stores.user_id = #{map.userId,jdbcType=INTEGER} and setRecords.traded_at is not null
and drivers.driver_name like '%${map.driverName}%'
and DATE(setRecords.traded_at) = #{map.tradedAt,jdbcType=TIMESTAMP}
